mapper.update:
update review_board
rv_date=sysdate, rv_pic=#{rvPic}, rv_content=#{rvContent}, rv_sp=#{rvSp}, rv_cost=#{rvCost}, rv_title=#{rvTitle},
where rv_num=#{rvNum}

update review_board
set rv_cnt = rv_cnt +1
where rv_num=#{rvNum}

update review_board
set rv_like = rv_like +1
where rv_num=#{rvNum}

update review board
set rv_sp = avg(rv_sp+#{rvSP})
where rv_num=#{rvNum}
